INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, '2. Начальник служби БзП під час організації польотів зобов’язаний:', $$контролювати дотримання особовим складом вимог нормативних документів з БзП, надавати допомогу керівному складу частини з питань запобігання авіаційним подіям та інцидентам;
