INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, '2. У цих Правилах терміни вживаються у таких значеннях:', $$авіаційна частина - суб’єкт державної авіації (далі - СДА), який має на оснащенні (на озброєнні) повітряні судна (далі - ПС), здійснює їх льотну, технічну експлуатацію, а також усі види забезпечення польотів;
