INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, '2. Черговий з АТЗ польотів під час польотів зобов’язаний:', $$організувати своєчасне подання до ПС ЗАТО ПС, АЗУ та інших матеріальних засобів;
INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, '2. Черговий лікар (фельдшер) під час польотів зобов’язаний:', $$проводити міжпольотний і післяпольотний медичний контроль льотного складу;
