INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, 'V. Розвідка погоди', NULL, 1);
INSERT INTO guide_sections (document_id, parent_id, title, content, order_num) VALUES (1, NULL, 'VII. Загальний порядок проведення польотів', $$1. Польоти дозволяється проводити за наявності не менше одного запасного аеродрому (при УМП - не менше двох) та готовності запасної посадкової смуги. Рішення на виконання польотів у СМУ при УМП приймається з урахуванням фактичного експлуатаційного мінімуму аеродрому для посадки відповідного типу ПС. 2. Польоти вертольотів без наявності запасного аеродрому дозволяється проводити в районі аеродрому в ПМУ та СМУ, а поза аеродромом - у ПМУ. Польоти легких літаків класів 4, 5 категорії А без наявності запасного аеродрому дозволяється проводити в районі аеродрому в ПМУ та СМУ у разі наявності в районі аеродрому злітно-посадкових майданчиків.$$, 2);
